// Optional avoidant sorting sol'n
impl Solution {
    pub fn divide_array(mut nums: Vec<i32>, k: i32) -> Vec<Vec<i32>> {
        nums.sort_unstable();
        if nums
            .chunks_exact(3)
            .all(|triple| triple[2] - triple[0] <= k)
        {
            nums.chunks_exact(3).map(|triple| triple.to_vec()).collect()
        } else {
            vec![]
        }
    }
}

