// https://leetcode.com/problems/number-of-substrings-containing-all-three-characters/

pub struct Solution;

impl Solution {
    pub fn number_of_substrings(s: String) -> i32 {
        let s = s.as_bytes();
        let len = s.len() as u16;
        let mut count = 0;
        let mut left = 0;
        let mut char_counts = [0; 3];
        let mut char_set = 0u8;
        for right in 0..len {
            let b = s[right as usize];
            let k = b - b'a';
            char_counts[k as usize] += 1;
            char_set |= 1 << k;
            while char_set == 7 {
                count += (len - right) as i32;
                let b = s[left as usize];
                left += 1;
                let k = b - b'a';
                char_counts[k as usize] -= 1;
                if char_counts[k as usize] == 0 {
                    char_set &= !(1 << k);
                    break;
                }
            }
        }
        count
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    fn test(s: &str, expected: i32) {
        assert!(s.len() >= 3);
        assert!(s.len() <= 50_000);
        for &b in s.as_bytes() {
            assert!(b >= b'a' && b <= b'c');
        }
        assert_eq!(Solution::number_of_substrings(s.to_owned()), expected);
    }

    #[test]
    fn ex1() {
        test("abcabc", 10)
    }

    #[test]
    fn ex2() {
        test("aaacb", 3)
    }

    #[test]
    fn ex3() {
        test("abc", 1)
    }

    #[test]
    fn discussion_case1() {
        test("", 45830534)
    }
}
