// https://leetcode.com/problems/take-k-of-each-character-from-left-and-right/

pub struct Solution;

impl Solution {
    pub fn take_characters(s: String, k: i32) -> i32 {
        assert!(k >= 0);
        if k == 0 {
            return 0;
        }
        assert!(k <= s.len() as i32);
        let s = s.as_bytes();
        let mut outside = [0i32; 3];
        for b in s {
            outside[(b - b'a') as usize] += 1;
        }
        if !outside.iter().all(|&x| x >= k) {
            -1
        } else {
            let mut left = 0;
            let mut right = 0;
            let mut max_window_size = 0;
            while right < s.len() {
                outside[(s[right] - b'a') as usize] -= 1;
                while left <= right && outside[(s[right] - b'a') as usize] < k {
                    outside[(s[left] - b'a') as usize] += 1;
                    left += 1;
                }
                right += 1;
                if right - left > max_window_size && outside.iter().all(|&x| x >= k) {
                    max_window_size = right - left;
                }
            }
            s.len() as i32 - max_window_size as i32
        }
    }
}

#[cfg(test)]
mod tests {
    use super::*;

    fn test(s: &str, k: i32, expected: i32) {
        assert!(s.len() >= 1);
        assert!(s.len() <= 100_000);
        assert!(k >= 0);
        assert!(k <= s.len() as i32);
        for b in s.bytes() {
            assert!(b == b'a' || b == b'b' || b == b'c');
        }
        assert_eq!(Solution::take_characters(s.to_string(), k), expected);
    }

    #[test]
    fn ex1() {
        test("aabaaaacaabc", 2, 8)
    }

    #[test]
    fn ex2() {
        test("a", 1, -1)
    }
    #[test]
    fn discussion_case0() {
        test("a", 0, 0)
    }

    #[test]
    fn discussion_case1() {
        test("abc", 0, 0)
    }

    #[test]
    fn discussion_case2() {
        test("abcabcabc", 2, 6)
    }

    #[test]
    fn discussion_case3() {
        test("aabbcc", 3, -1)
    }

    #[test]
    fn discussion_case4() {
        test("aaaaabbbbbccccc", 15, -1)
    }

    #[test]
    fn discussion_case5() {
        test("aaaaabbbb", 3, -1)
    }

    #[test]
    fn discussion_case6() {
        test("abcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbc", 1, 3)
    }

    #[test]
    fn discussion_case7() {
        test("bcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbcbca", 1, 3)
    }

    #[test]
    fn discussion_case8() {
        test("aaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaabc", 1, 3)
    }

    #[test]
    fn discussion_case9() {
        test("bcaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaaa", 1, 3)
    }

    #[test]
    fn discussion_case10() {
        test("cacbcc", 1, 4)
    }

    #[test]
    fn discussion_case11() {
        test("", 49, -1)
    }

    #[test]
    fn discussion_case12() {
        test("aaaaaaabccbaa", 1, 4)
    }

    #[test]
    fn discussion_case13() {
        test(
            "",
            5780, 17347)
    }
}
